import React, { useContext } from "react";
import { CarrinhoContext } from "../../Global/CarrinhoContext"
import { Container, Cartcontainer } from "./Carrinho.style"

const Carrinho = () => {
    const [cart, setCart]  = useContext(CarrinhoContext);
    
    return (

        <Container className="container">
            <Cartcontainer className="cartcontainer">

              
                            
                            <span className="cart-produto-name" > itens no carrinho: {cart.lenght} </span>
                            <span className="cart-produto-price" > 0 {contextValue}</span>
                            
                        
                    

            </Cartcontainer>
        </Container>


    )
}

export default Carrinho;
