    const contextValue = useContext(CarrinhoContext);

    return (




        <div>
            <span className="cart-produto-name" > 0 {contextValue}</span>
            <span className="cart-produto-price" > 0 {contextValue}</span>
        </div>








    )
}

export default Carrinho;
