import React, { useContext } from "react";
import { CarrinhoContext } from "../../Global/CarrinhoContext"


export const Carrinho = () => {
    const [cart, setCart]  = useContext(CarrinhoContext);
    
    return (

        <div className="container">
            <div className="cartcontainer">

              
                            
                            <span className="cart-produto-name" > itens no carrinho: {cart.lenght} </span>
                            <span className="cart-produto-price" > 0 </span>
                            
                        
                    

            </div>
        </div>


    )
}

export default Carrinho;
