import React, {createContext, useReducer} from "react"

import {CarrinhoReducer} from "../Global/CarrinhoReducer"
export const CarrinhoContext = createContext();

const CarrinhoContextProvider = (props) => {

    const [cart, dispatch] = useReducer(CarrinhoReducer, {shoppingCart: [],
         totalPrice: 0, qty: 0});

    return(
        <CarrinhoContext.Provider value={{...cart, dispatch}}>
            {props.children}
        </CarrinhoContext.Provider>
    )
}

export default CarrinhoContextProvider;
