import React, { useContext } from "react";
import { CarrinhoContext } from "../../Global/CarrinhoContext"
import { Container, Cartcontainer } from "./Carrinho.style"

const Carrinho = () => {
    const {shoppingCart, totalPrice, qty, dispatch} = useContext(CarrinhoContext);
    
    return (

        <Container className="container">
            <Cartcontainer className="cartcontainer">

                {shoppingCart.lenght > 0 ? 
                    shoppingCart.map(cart =>(
                        <div className="cart" key={cart.id}>
                            <div className="cart-image"><img src={cart.image} alt="not found" /></div>
                            <span className="cart-produto-name" > {cart.name}</span>
                            <span className="cart-produto-price" >{cart.price}</span>
                        </div>
                    ))
                : 'Desculpa seu carrinho esta vazio'}

            </Cartcontainer>
        </Container>


    )
}

export default Carrinho;
