export const CarrinhoReducer = (state, action) => {

    const {shoppingCart, totalPrice, qty} = state
    let produtos;
    let index;
    let updatePrice;
    let updateQty;
    switch(action.type){

        case 'ADD_TO_CART':
            const check = shoppingCart.find(produtos => produtos.id == action.id);
            if(check){
                return state;
            } else {
                produtos = action.produtos;
                produtos['qty']= 1;
                updateQty = qty + 1;
                updatePrice = totalPrice + produtos.price;
                return {shoppingCart: [produtos, ...shoppingCart], totalPrice:
                     updatePrice, qty: updateQty}
            }
            break;

            default:
            return state;
    }

}
